import React, {useEffect, useState}from 'react'
import image from '../images/user.jpeg'
import Edit from "../images/edit.png";
import Delete from "../images/delete.png";
import { Link, useLocation} from "react-router-dom";
import Menu from "../components/Menu";
import axios from "axios";

const Single = () => {

    const[posts, setPosts] = useState({})

    const location = useLocation()
    const postId = location.pathname.split('/')[2]

    useEffect(() => {
      const fetchData = async () => {
        try{
          const res = await axios.get(`/posts/${postId}`);
          console.log(res);
          setPosts(res.data)
        }catch(e){
          console.log(e)
        }
      }
      fetchData()
    },[postId])
    return (
        <div className='single'>
            <div className="content">
                <img src="https://images.pexels.com/photos/7008010/pexels-photo-7008010.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2" alt=""/>
                <div className="user">
                    <img src={image} alt="" />
                    <div className="info">
                        <span>Jason Li</span>
                        <p>Posted 2 days ago</p>
                    </div>
                    <div className="edit">
                        <Link to={`/write?edit=2`}>
                            <img src={Edit} alt="" />
                        </Link>
                        <img src={Delete} alt="" />
                    </div>
                </div>
                <h1>Lorem ipsum dolor sit amet.</h1>
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Temporibus minima autem enim tempore obcaecati quibusdam quo aliquam animi. Ipsam, quam rem voluptatem optio commodi quia. Tenetur, officiis voluptas reprehenderit molestiae dignissimos deserunt assumenda quis! Neque, doloribus inventore. Odit, repellat nemo magnam tempore illo deserunt ex itaque amet magni iusto, culpa dolores sed quidem id, fuga architecto modi sequi? Velit, eligendi aperiam. Excepturi a modi maiores, rerum iure ipsa quibusdam itaque labore provident ducimus cum vero perspiciatis, illum debitis ea alias quia obcaecati at dolorem non, unde consectetur laudantium. Itaque nesciunt porro facilis cum, a eos! Porro neque molestias deleniti doloremque.</p>
            </div>
            <Menu/>
        </div>
    )
}

export default Single;
